import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import RestoreIcon from '@material-ui/icons/Restore';
import FavoriteIcon from '@material-ui/icons/Favorite';
import LocationOnIcon from '@material-ui/icons/LocationOn';

const useStyles = makeStyles({
  root: {
    // position: 'absolute',
    bottom: '0',
    left: '0',
    right: '0',
    background: '#3f51b5',
  },
  icon:{
    color: 'white',
  }
});

export default function SimpleBottomNavigation() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  return (
    <BottomNavigation
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      showLabels
      className={classes.root}
    >
      <BottomNavigationAction className={classes.icon} label="Global" icon={<RestoreIcon />} />
      <BottomNavigationAction className={classes.icon} label="Country" icon={<FavoriteIcon />} />
      <BottomNavigationAction className={classes.icon} label="Nearby" icon={<LocationOnIcon />} />
    </BottomNavigation>
  );
}
